import React, {useState, useEffect} from "react";
import  { fetchAllPlayers } from "./APIFetch";
import { PlayerForm } from "./PlayerForm";
import { PlayerList } from "./PlayerList";
import "./style.css";


const Main = () => {
    const [players, setPlayers] = useState([]);

    useEffect(() => {
        const getPlayers = async () => {
            try {
                const data = await fetchAllPlayers();
                    setPlayers(data);
            } catch (error) {
                console.error(error);
            }
        }
}, []);

return (
    <>
    <div> <PlayerForm players={players} setPlayers={setPlayers} /> </div>
    {/* <div> <PlayerList players={players} setPlayers={setPlayers} /> </div> */}
    {players.map((player) => 
    <div class="single-player-card">
        <div class="header-info">
          <p class="pup-title">{player.name}</p>
          <p class="pup-number">{players.id}</p>
        </div>
        <img src={players.imageUrl} alt="photo of ${pup.name} the puppy" />
        <button class="detail-button" data-id={players.id}>See details</button>
        <button class="delete-button" data-id={players.id}>Remove from roster</button>
      </div>
    )}
    </>
)
}

export default Main;
